import org.junit.Before;import org.junit.Test;import static org.junit.Assert.*;import picocli.CommandLine;import java.math.BigDecimal;public class CommandLineAppTest {    private CommandLineApp cla;    @Before    public void setUp(){        cla = new CommandLineApp();    }    @Test    public void inputLenghtOfMessageTest(){        String[] args = {"-l", "20"};        new CommandLine(cla).parse(args);        assertEquals(20,cla.smsLength);    }    @Test    public void inputUnitPriceTest(){        String[] args = {"-p", "0.14"};        new CommandLine(cla).parse(args);        assertEquals(new BigDecimal("0.14"),cla.unitPrice);    }    @Test    public void inputUseOptimalCompressorTest(){        String[] args = {"-o"};        new CommandLine(cla).parse(args);        assertTrue(cla.useOptimalCompressor);    }}